INSERT INTO `by_doc` VALUES ('9', '渝辣记火锅米线', '1', '1', '', '“渝辣记火锅米线”是重庆索旺餐饮旗下除“Y火锅”“嘿小面”的又一连锁餐饮品牌，通过对重庆地域美食火锅的创新型研发，推出新型火锅快餐系列产品“火锅米线”，并在完善产品线、经营团队和配送模式后，正式启动了全国连锁征程！', '', '', '/Uploads/attachment/image/20150731/55bae739912b4.png', '1', '', '76', '0', '1443085432', '1438311272', '1', '0', '0', '7', '6,7', '0', '0', '1', null);
