INSERT INTO `by_doc` VALUES ('11', '重庆吴优火锅', '1', '1', '', '重庆旭康餐饮管理有限公司成立于2008年，旗下拥有重庆吴优火锅、重庆黑山谷天骄度假村两大实力品牌，组织有自己的研发团队，深加工团队，经营团队。从加工到经营都有完善的供应系统管理体系，全部连锁直营店、加盟连锁店实行统一经营，从而保证品牌系统化。', '', '', '/Uploads/attachment/image/20150731/55bae813be353.png', '1', '', '101', '0', '1438312575', '1438312575', '1', '0', '0', '7', '6,7', '0', '0', '1', null);
