INSERT INTO `by_doc` VALUES ('87', '采用左右值编码来存储无限分级树形结构的数据库表设计', '1', '1', '', '该设计方案的优点是：只用一条查询语句即可得到某个根节点及其所有子孙节点的先序遍历。由于消除了递归，在数据记录量较大时，可以大大提高列表效率。但是，这种编码方案由于层信息位数的限制，限制了每层能所允许的最大子节点数量及最大层数。同时，在添加新节点的时候必须先计算新节点的位置是否超过最大限制。', '', '', '', '1', '', '36', '0', '1454227839', '1452483031', '1', '0', '0', '2', '1,2', '0', '0', '1', 'PHP');
