INSERT INTO `by_doc_content` VALUES ('00000000005', '&lt;p&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-family:Microsoft YaHei;color:#60d978;font-size:18px;&quot;&gt;页面二维码的生成步骤：&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;font-size:16px;&quot;&gt;&lt;strong&gt;一、自封装函数用于获取页面的URLK地址：&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;/**&lt;br /&gt;\r\n&amp;nbsp; *获取当前的URL地址&lt;br /&gt;\r\n&amp;nbsp; &lt;a href=&quot;mailto:*@author&quot;&gt;*@author&lt;/a&gt; xiang 2015-07-10&lt;br /&gt;\r\n&amp;nbsp; */&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;function &lt;span style=&quot;color:#9933e5;&quot;&gt;&lt;strong&gt;curPageURL&lt;/strong&gt;&lt;/span&gt;() &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;{&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $pageURL = \'http\';&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; if ($_SERVER[&quot;HTTPS&quot;] == &quot;on&quot;) &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $pageURL .= &quot;s&quot;;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $pageURL .= &quot;://&quot;;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; if ($_SERVER[&quot;SERVER_PORT&quot;] != &quot;80&quot;) &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $pageURL .= $_SERVER[&quot;SERVER_NAME&quot;] . &quot;:&quot; . $_SERVER[&quot;SERVER_PORT&quot;] . $_SERVER[&quot;REQUEST_URI&quot;];&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; } &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; else &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $pageURL .= $_SERVER[&quot;SERVER_NAME&quot;] . $_SERVER[&quot;REQUEST_URI&quot;];&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; return $pageURL;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;}&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;font-size:16px;&quot;&gt;&lt;strong&gt;二、加载二维码库，实现生成二维码的函数&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;/**&lt;br /&gt;\r\n&amp;nbsp; *获取当前的URL地址&lt;br /&gt;\r\n&amp;nbsp; &lt;a href=&quot;mailto:*@author&quot;&gt;*@author&lt;/a&gt; xiang 2015-07-10&lt;br /&gt;\r\n&amp;nbsp; */\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;span style=&quot;color:#009900;&quot;&gt;function &lt;span style=&quot;background-color:#ffffff;color:#9933e5;&quot;&gt;&lt;strong&gt;code_create&lt;/strong&gt;&lt;/span&gt;($url){&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;include &quot;&lt;strong&gt;phpqrcode.php&lt;/strong&gt;&quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&lt;span style=&quot;color:#e56600;&quot;&gt;网上能够下载到该库，加载这个库&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; QRcode::png($url);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //调用QRcode方法生成二维码&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;}&amp;nbsp;&amp;nbsp;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;font-size:16px;&quot;&gt;&lt;strong&gt;三、在页面对用的控制器里面写入一个方法用于转换url的格式&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;&quot;&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt;&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	class NewsController&amp;nbsp; extends ComController {\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; public function msg(){&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;$this-&amp;gt;display();&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//二维码&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#009900;&quot;&gt;public function qrcode(){&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;$url = I(\'url\');&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url = base64_decode($url);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//进行转换&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo code_create($url);&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;/span&gt;&lt;br /&gt;\r\n}\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;font-size:16px;&quot;&gt;&lt;strong&gt;四、页面中使用函数获取到当前的url地址，然后通过img标签调用对用的控制器中的方法并且传参到控制器中，加载二维码图像。&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#ffffff;&quot;&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;line-height:1.5;&quot;&gt;&amp;nbsp;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#009900;&quot;&gt; &amp;lt;!--二维码区域--&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;?php&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $url= &lt;span style=&quot;color:#9933e5;&quot;&gt;curPageURL();&amp;nbsp; &lt;/span&gt;//得到当前的URL地址&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ?&amp;gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;color:#9933e5;&quot;&gt;&amp;lt;img src=&quot;{:U(\'News/qrcode\', &quot;url=&quot;.base64_encode($url))}&quot;/&amp;gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;p style=&quot;font-size:12px;&quot;&amp;gt;微信扫一扫，分享该页面！&amp;lt;/p&amp;gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;', '');
INSERT INTO `by_doc_content` VALUES ('00000000006', '&lt;table id=&quot;blogContentTable&quot; style=&quot;margin:0px;padding:0px;&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;top&quot;&gt;\r\n				&lt;div id=&quot;blogContainer&quot; style=&quot;margin:0px;padding:0px 16px;&quot;&gt;\r\n					&lt;div id=&quot;blogDetailDiv&quot; style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n						&lt;div class=&quot;blog_details_20120222&quot; style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n							&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n								&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n									&lt;span style=&quot;color:#E56600;&quot;&gt;Jquery在异步提交方面封装的很好，直接用AJAX非常麻烦，Jquery大大简化了我们的操作，不用考虑浏览器的诧异了。&lt;/span&gt;&lt;br /&gt;\r\n								&lt;/div&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#E56600;&quot;&gt;推荐一篇不错的jQuery Ajax 实例文章，忘记了可以去看看：&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									地址为：&lt;a href=&quot;http://www.cnblogs.com/yeer/archive/2009/07/23/1529460.html&quot; target=&quot;_blank&quot;&gt;http://www.cnblogs.com/yeer/archive/2009/07/23/1529460.html&lt;/a&gt;&amp;nbsp;和&amp;nbsp;&lt;a href=&quot;http://www.w3school.com.cn/jquery/&quot; target=&quot;_blank&quot;&gt;http://www.w3school.com.cn/jquery/&lt;/a&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									$.post、$.get是一些简单的方法，如果要处理复杂的逻辑，还是需要用到jQuery.ajax()\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									一、$.ajax的一般格式\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;$.ajax({&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; type: \'POST\',&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; url:&amp;nbsp;url&amp;nbsp;,&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; data:&amp;nbsp;data&amp;nbsp;,&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; success:&amp;nbsp;success&amp;nbsp;,&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; dataType:&amp;nbsp;dataType&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#4C33E5;&quot;&gt;});&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									二、$.ajax的参数描述\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;参数 描述&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;table style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n									&lt;tbody&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;url&amp;nbsp;&lt;/span&gt; \r\n											&lt;/td&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;必需。规定把请求发送到哪个 URL。&lt;/span&gt; \r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;data&lt;/span&gt; \r\n											&lt;/td&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;可选。映射或字符串值。规定连同请求发送到服务器的数据。&lt;/span&gt; \r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;success(data, textStatus, jqXHR)&lt;/span&gt; \r\n											&lt;/td&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;可选。请求成功时执行的回调函数。&lt;/span&gt; \r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n										&lt;tr&gt;\r\n											&lt;td&gt;\r\n												&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;dataType&lt;/span&gt; \r\n											&lt;/td&gt;\r\n											&lt;td&gt;\r\n												&lt;p&gt;\r\n													&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;可选。规定预期的服务器响应的数据类型。&lt;/span&gt; \r\n												&lt;/p&gt;\r\n												&lt;p&gt;\r\n													&lt;span style=&quot;color:#FFFFFF;background-color:#666666;&quot;&gt;默认执行智能判断（xml、json、script 或 html）。&lt;/span&gt; \r\n												&lt;/p&gt;\r\n											&lt;/td&gt;\r\n										&lt;/tr&gt;\r\n									&lt;/tbody&gt;\r\n								&lt;/table&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									三、$.ajax需要注意的一些地方：\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp;1.data主要方式有三种，html拼接的，json数组，form表单经serialize()序列化的；通过dataType指定，不指定智能判断。&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&lt;span style=&quot;color:#009900;&quot;&gt;&amp;nbsp; 2.$.ajax只提交form以文本方式，如果异步提交包含&amp;lt;file&amp;gt;上传是传过不过去,需要使用jquery.form.js的$.ajaxSubmit&lt;/span&gt; \r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									&amp;nbsp;\r\n								&lt;/p&gt;\r\n								&lt;p&gt;\r\n									四、$.ajax我的实际应用例子\r\n								&lt;/p&gt;\r\n								&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&lt;br /&gt;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&lt;br /&gt;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp;&amp;nbsp;//1.$.ajax带json数据的异步请求\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; var aj = $.ajax( { &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; url:\'productManager_reverseUpdate\',// 跳转到 action &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; data:{ &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; selRollBack : selRollBack, &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; selOperatorsCode : selOperatorsCode, &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; PROVINCECODE : PROVINCECODE, &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;pass2 : pass2 &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp; }, &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp;type:\'post\', &amp;nbsp;\r\n									&lt;/div&gt;\r\n									&lt;div style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n										&amp;nbsp; &amp;nbsp; &amp;nbsp;cache:false,