词设置&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;alt&quot;&gt;\r\n		&lt;span class=&quot;comment&quot;&gt;//&amp;nbsp;$key_array&amp;nbsp;=&amp;nbsp;array(&amp;nbsp;&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;&quot;&gt;\r\n		&lt;span class=&quot;comment&quot;&gt;//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(\'搜索\',&amp;nbsp;\'http://www.baidu.com\'),&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;alt&quot;&gt;\r\n		&lt;span class=&quot;comment&quot;&gt;//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(\'google\',&amp;nbsp;\'http://www.google.com\')&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;&quot;&gt;\r\n		&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;comment&quot;&gt;//&amp;nbsp;);&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;alt&quot;&gt;\r\n		&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;&quot;&gt;\r\n		&lt;span class=&quot;comment&quot;&gt;//在输出文章内容的地方调用这个函数&amp;nbsp;$str为文章内容&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;\r\n	&lt;/li&gt;\r\n	&lt;li class=&quot;alt&quot;&gt;\r\n		&lt;span class=&quot;func&quot; style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;echo&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;&amp;nbsp;keyword_replace(&lt;/strong&gt;&lt;/span&gt;&lt;span class=&quot;func&quot; style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;stripslashes&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;(&lt;/strong&gt;&lt;/span&gt;&lt;span class=&quot;vars&quot; style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;$str&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;),&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;span class=&quot;vars&quot; style=&quot;color:#E56600;&quot;&gt;&lt;strong&gt;$key_array&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:#E56600;&quot;&gt;&lt;