INSERT INTO `by_doc_content` VALUES ('00000000052', '&lt;pre&gt;/**\r\n * 对二维数组进行排序\r\n * @param array $list 二维数组\r\n * @param string $field 排序的字段名\r\n * @param string $sortby 排序类型 asc正向排序 desc逆向排序 nat自然排序\r\n * @return array\r\n */\r\nfunction list_sort_by($list, $field, $sortby=\'asc\') {\r\n   if (is_array($list)) {\r\n       $refer = $resultSet = array();\r\n       foreach ($list as $i =&amp;gt; $data)\r\n           $refer[$i] = &amp;amp;$data[$field];\r\n       switch ($sortby) {\r\n           case \'asc\': // 正向排序\r\n                asort($refer);\r\n                break;\r\n           case \'desc\':// 逆向排序\r\n                arsort($refer);\r\n                break;\r\n           case \'nat\': // 自然排序\r\n                natcasesort($refer);\r\n                break;\r\n       }\r\n       foreach ( $refer as $key=&amp;gt; $val)\r\n           $resultSet[] = &amp;amp;$list[$key];\r\n       return $resultSet;\r\n   }\r\n   return false;\r\n}&lt;/pre&gt;', '');
INSERT INTO `by_doc_content` VALUES ('00000000054', '&lt;h1&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;TP修复数据表&lt;/span&gt; \r\n&lt;/h1&gt;\r\n&lt;pre&gt;use Think\\Db;\r\n\r\n/**\r\n * 修复表\r\n * @param string $tables 表名\r\n */\r\npublic function repair($tables = \'\'){\r\n    if($tables) {\r\n        $Db   = Db::getInstance();\r\n        $list = $Db-&amp;gt;query(&quot;REPAIR TABLE `{$tables}`&quot;);\r\n        if($list){\r\n            $this-&amp;gt;success(&quot;数据表\'{$tables}\'修复完成！&quot;);\r\n        } else {\r\n            $this-&amp;gt;error(&quot;数据表\'{$tables}\'修复出错请重试！&quot;);\r\n        }\r\n    } else {\r\n        $this-&amp;gt;error(&quot;请指定要修复的表！&quot;);\r\n    }\r\n}&lt;/pre&gt;\r\n&lt;h1&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;TP优化数据表&lt;/span&gt; \r\n&lt;/h1&gt;\r\n&lt;pre&gt;use Think\\Db;\r\n\r\n/**\r\n * 优化表\r\n * @param string $tables 表名\r\n */\r\npublic function optimize($tables = \'\'){\r\n    if($tables) {\r\n        $Db   = Db::getInstance();\r\n        $list = $Db-&amp;gt;query(&quot;OPTIMIZE TABLE `{$tables}`&quot;);\r\n        if($list){\r\n            $this-&amp;gt;success(&quot;数据表\'{$tables}\'优化完成！&quot;);\r\n        } else {\r\n            $this-&amp;gt;error(&quot;数据表\'{$tables}\'优化出错请重试！&quot;);\r\n        }\r\n    } else {\r\n        $this-&amp;gt;error(&quot;请指定要优化的表！&quot;);\r\n    }\r\n}&lt;/pre&gt;', '');
