INSERT INTO `by_doc_content` VALUES ('00000000062', '&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#009900;color:#FFFFFF;&quot;&gt;-|&amp;lt;还在研究-不知道文章是否详解&amp;gt;|-&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	现在流行的网站支持平台,支付宝当仁不让的老大了,现在我们就来告诉你如何使用支付宝api来做第三方支付,把支付宝放到自己网站来,代码如下:\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;alipay_config.php&lt;/strong&gt;配置程序如下：\r\n&lt;/p&gt;\r\n&lt;div class=&quot;codetitle&quot;&gt;\r\n	代码如下:\r\n&lt;/div&gt;\r\n&lt;div class=&quot;codebody&quot; id=&quot;code68474&quot;&gt;\r\n	&amp;lt;?php &lt;br /&gt;\r\n*/ &lt;br /&gt;\r\n//alipay_config.php 配置程序 &lt;br /&gt;\r\n$interfaceurl = &quot;https://www.alipay.com/payto:&quot;; &lt;br /&gt;\r\n$sitename&amp;nbsp; = &quot;网站名称&quot;; &lt;br /&gt;\r\n$weburl&amp;nbsp;&amp;nbsp; = &quot;http://网站网址&quot;; &lt;br /&gt;\r\n$o_fee&amp;nbsp;&amp;nbsp; = &quot;0.00&quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //平邮费 &lt;br /&gt;\r\n$e_fee&amp;nbsp;&amp;nbsp; = &quot;0.00&quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //快递费 &lt;br /&gt;\r\n$selleremail&amp;nbsp; = &quot;&quot;;//支付宝账号 &lt;br /&gt;\r\n$payalikey&amp;nbsp; = &quot;&quot;;//安全校验码 &lt;br /&gt;\r\n$imgurl&amp;nbsp;&amp;nbsp; = &quot;pay.gif&quot;; //按钮图片源 &lt;br /&gt;\r\n$imgtitle&amp;nbsp; = &quot;使用支付宝购买&quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //按钮图片说明 &lt;br /&gt;\r\n?&amp;gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;alipay.php&lt;/strong&gt;代码如下：&lt;br /&gt;\r\n&lt;div class=&quot;codebody&quot; id=&quot;code60189&quot;&gt;\r\n	&amp;lt;?php &lt;br /&gt;\r\n/********************************************************************* &lt;br /&gt;\r\n&amp;nbsp;filename: alipay.php &lt;br /&gt;\r\n&amp;nbsp;author:&amp;nbsp; dboyzhang &lt;br /&gt;\r\n&amp;nbsp;version:&amp;nbsp; ver 2.0.0 beta1 &lt;br /&gt;\r\n&amp;nbsp;contact_me: wangwang:dboyzhang &lt;br /&gt;\r\n*********************************************************************/ &lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n//&lt;strong&gt;alipay.php&lt;/strong&gt;代码 &lt;br /&gt;\r\nrequire_once(&quot;alipay_config.php&quot;); &lt;br /&gt;\r\nclass alipay &lt;br /&gt;\r\n{ &lt;br /&gt;\r\n&amp;nbsp;function geturl($s1,$s2,$s3,$s4,$s5,$s6,$s7,$s8,$s9,$s10,$s11,$s12,$s13,$s14,$s15,$s16,$s17,$s18,$s19,$s20,$s21,$s22,$s23) &lt;br /&gt;\r\n&amp;nbsp;{ &lt;br /&gt;\r\n&amp;nbsp; $parameter = array( &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'cmd\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s1, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'subject\'&amp;nbsp; =&amp;gt; $s2, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'body\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s3, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'order_no\'&amp;nbsp; =&amp;gt; $s4, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'price\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s5, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'url\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s6, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'type\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s7, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'number\'&amp;nbsp; =&amp;gt; $s8, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'transport\'&amp;nbsp; =&amp;gt; $s9, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'ordinary_fee\'&amp;nbsp; =&amp;gt; $s10, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'express_fee\'&amp;nbsp; =&amp;gt; $s11, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'readonly\'&amp;nbsp; =&amp;gt; $s12, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_msg\'&amp;nbsp; =&amp;gt; $s13, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'seller\'&amp;nbsp; =&amp;gt; $s14, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer\'&amp;nbsp;&amp;nbsp; =&amp;gt; $s15, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_name\'&amp;nbsp; =&amp;gt; $s16, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_address\'&amp;nbsp; =&amp;gt; $s17, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_zipcode\'&amp;nbsp; =&amp;gt; $s18, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_tel\'&amp;nbsp; =&amp;gt; $s19, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'buyer_mobile\'&amp;nbsp; =&amp;gt; $s20, &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; \'partner\'&amp;nbsp; =&amp;gt; $s21, &lt;br /&gt;\r\n&amp;nbsp; ); &lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; $url = $s22.$s14.&quot;?&quot;; &lt;br /&gt;\r\n&amp;nbsp; foreach($parameter as $key =&amp;gt; $value){ &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; if($value){ &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $url&amp;nbsp; .= $key.&quot;=&quot;.urlencode($value).&quot;&amp;amp;&quot;; &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $acsouce .=$key.$value; &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; } &lt;br /&gt;\r\n&amp;nbsp; } &lt;br /&gt;\r\n&amp;nbsp; $url&amp;nbsp; .= \'ac=\'.md5($acsouce.$s23); &lt;br /&gt;\r\n&amp;nbsp; return $url; &lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;} &lt;br /&gt;\r\n} &lt;br /&gt;\r\n?&amp;gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;pay.php&lt;/strong&gt;页面如下：\r\n&lt;div class=&quot;codebody&quot; id=&quot;code38162&quot;&gt;\r\n	&amp;lt;?php&lt;br /&gt;\r\nerror_reporting(0); &lt;br /&gt;\r\n$aliname=$_POST[&quot;aliname&quot;];&amp;nbsp; &lt;br /&gt;\r\n$alizipcode=$_POST[&quot;alizipcode&quot;];&amp;nbsp; &lt;br /&gt;\r\n$aliphone=$_POST[&quot;aliphone&quot;];&amp;nbsp; &lt;br /&gt;\r\n$aliaddress=$_POST[&quot;aliaddress&quot;];&amp;nbsp; &lt;br /&gt;\r\n$aliorder=$_POST[&quot;aliorder&quot;];&amp;nbsp; &lt;br /&gt;\r\n$alimailtype=$_POST[&quot;alimailtype&quot;];&amp;nbsp; &lt;br /&gt;\r\n$alimoney=$_POST[&quot;alimoney&quot;];&amp;nbsp; &lt;br /&gt;\r\n$alimob=$_POST[&quot;alimob&quot;];&amp;nbsp; &lt;br /&gt;\r\n$alibody=$_POST[&quot;ali