INSERT INTO `by_doc_content` VALUES ('00000000065', '&lt;h3 style=&quot;text-align:left;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	php中执行java代码参考文章：&lt;a href=&quot;http://www.fkblog.org/blog562&quot;&gt;php中执行java代码&lt;/a&gt; \r\n&lt;/h3&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	&lt;strong&gt;echsop开发交通银行接口&lt;/strong&gt;步骤:\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;text-align:left;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	步骤1、在languages/zh_cn/payment/bcm.php 添加语言文件（可参考其他银行）\r\n&lt;/h3&gt;\r\n&lt;h3 style=&quot;text-align:left;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	步骤2、创建includes/modules/payment/bcm 目录 将相关的配置文件及jar包拷贝进去\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	目录结构如下图\r\n&lt;/p&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;/Uploads/doc/image/2015-09-28/56094d093e603.jpg&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;ul style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	&lt;li&gt;\r\n		cert目录为存放证书目录\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		ini/B2CMerchant.xml为证书路径与提交url及日志等配置文件目录\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		java/Java.ini为JavaBridge 的配置文件\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		lib为jar包目录\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		log为日志目录\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h3 style=&quot;text-align:left;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	步骤3、参考php中执行java代码文章启动JavaBridge（确保为8080端口，否则要修改java/Java.ini（默认为8080）文件的端口）\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	如果已启动则可杀死对应的进程号\r\n&lt;/p&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	windows：\r\n&lt;/p&gt;\r\n&lt;pre class=&quot;wp-code-highlight prettyprint prettyprinted&quot;&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;netstat &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;-&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;nao &lt;/span&gt;&lt;span style=&quot;color:#880000;&quot; class=&quot;com&quot;&gt;//列出所有进程以及其占用的端口&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;找到&lt;/span&gt;&lt;span style=&quot;color:#006666;&quot; class=&quot;lit&quot;&gt;8080&lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;的进程号&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt; taskkill &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;-&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;PID &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;进程号&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;-&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;F &lt;/span&gt;&lt;span style=&quot;color:#880000;&quot; class=&quot;com&quot;&gt;//强制关闭8080的进程号&lt;/span&gt;&lt;/pre&gt;\r\n&lt;p style=&quot;background-color:#ffffff;text-indent:0px;color:#333333;&quot;&gt;\r\n	linux：\r\n&lt;/p&gt;\r\n&lt;pre class=&quot;wp-code-highlight prettyprint prettyprinted&quot;&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;netstat &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;-&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt;naop &lt;/span&gt;&lt;span style=&quot;color:#666600;&quot; class=&quot;pun&quot;&gt;|&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot; class=&quot;pln&quot;&gt; grep &lt;/span&gt;&lt;span style=&quot;color:#006