INSERT INTO `by_doc_content` VALUES ('00000000069', '&lt;span style=&quot;background-color:#009900;color:#FFFFFF;&quot;&gt;方法一：&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color:#EE33EE;&quot;&gt;/*通过替换的方式，将图片的宽和高替换成项目所需的尺寸*/&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp; /*&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp; *&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp; *编辑器中的图片处理&amp;nbsp; （手机中的自适应）&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp; **/&lt;br /&gt;\r\n&amp;nbsp;function changeParse($content){&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; return preg_replace(array(\'/(?:height|width|style)=(\\\'|&quot;).*?\\\\1/\',\'/&amp;lt;p[^&amp;gt;]+&amp;gt;/\'),array(&quot;style=\'width:100%\'&quot;,\'&amp;lt;p&amp;gt;\'),$content);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br /&gt;\r\n&lt;br /&gt;\r\n说明：本方法有不足之处，如果编辑器中的图片大小未制定，该方法将失效！&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div align=&quot;center&quot;&gt;\r\n	===================================================================&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#009900;color:#FFFFFF;&quot;&gt;方法二：&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color:#EE33EE;&quot;&gt;/*通过js来动态的控制div对应下的img图片的大小*/&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;lt;div class=&quot;content_div&quot;&amp;gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 文章详情内容&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;lt;/div&amp;gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n--------------------------------------------------------------------------------------------&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;lt;!--详情页中编辑器中旅的图片大小处理--&amp;gt;&lt;br /&gt;\r\n&amp;lt;script type=&quot;text/javascript&quot; src=&quot;js/jquery-1.9.min.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;br /&gt;\r\n&amp;lt;script type=&quot;text/javascript&quot;&amp;gt;&lt;br /&gt;\r\n$(function(){&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $(\'.content_div img\').css({&quot;width&quot;:&quot;100%&quot;});&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;}) ;&lt;br /&gt;\r\n&amp;lt;/script&amp;gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n（仅供参考，如有不妥之处，还请留言指正！感谢！）', '');
