INSERT INTO `by_doc_content` VALUES ('00000000074', '//处理 不带 www 的域名的转向&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;$the_host = $_SERVER[\'HTTP_HOST\'];//取得当前域名&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;$the_url = isset($_SERVER[\'REQUEST_URI\']) ? $_SERVER[\'REQUEST_URI\'] : \'\';//判断地址后面部分&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;$the_url = strtolower($the_url);//将英文字母转成小写&lt;br /&gt;\r\n&lt;br /&gt;\r\nif($the_url==&quot;/index.php&quot;)//判断是不是首页&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;$the_url=&quot;&quot;;//如果是首页,赋值为空&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&lt;br /&gt;\r\nif($the_host == \'jwzcq.com\'){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; header(\'HTTP/1.1 301 Moved Permanently\');//发出301头部&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; header(\'Location:http://www.jwzcq.com\'.$the_url);//跳转到带www的网址&lt;br /&gt;\r\n}&lt;br /&gt;', '');
INSERT INTO `by_doc_content` VALUES ('00000000075', '&lt;h1 style=&quot;font-family:Arial;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:10pt;&quot;&gt;MySQL JOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:10pt;&quot;&gt;语法概述&lt;/span&gt;&lt;/span&gt;\r\n&lt;/h1&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;SQL&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;（&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;MySQL&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;）&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;JOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;用于根据两个或多个表中的字段之间的关系，从这些表中得到数据。&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;JOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;通常与&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;ON&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;关键字搭配使用，基本语法如下：&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;pre&gt;&lt;span style=&quot;font-family:Tahoma, verdana;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;&lt;span&gt;... FROM table1 INNER|LEFT|RIGHT JOIN table2 ON conditiona&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/pre&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table1&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;通常称为左表，&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table2&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;称为右表。&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;ON&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;关键字用于设定匹配条件，用于限定在结果集合中想要哪些行。如果需要指定其他条件，后面可以加上&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_where.shtml&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;WHERE&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_where.shtml&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;条件&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&amp;nbsp;或者&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_limit.shtml&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;LIMIT&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;以限制记录返回数目等。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;下面以最常见的两表连接来说明&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;MySQLJOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;的用法，关于多表&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;JOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;请参见《&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_join_multi_table.shtml&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;MySQLJOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_join_multi_table.shtml&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;多表&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;》。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;h2 class=&quot;cjk&quot; style=&quot;font-family:Arial;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;a name=&quot;t1&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:10pt;&quot;&gt;MySQLJOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:10pt;&quot;&gt;分类&lt;/span&gt;&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;JOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;按照功能大致分为如下三类：&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;ul style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_inner_join.shtml#inner%20join&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;INNERJOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;（内连接）：取得两个表中存在连接匹配关系的记录。&lt;/span&gt;\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_left_join.shtml&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;LEFTJOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;（左连接）：取得左表（&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table1&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;）完全记录，即是右表（&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table2&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;）并无对应匹配记录。&lt;/span&gt;\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p&gt;\r\n			&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;a href=&quot;http://www.5idev.com/p-php_mysql_right_join.shtml&quot;&gt;&lt;span style=&quot;color:#006EC6;&quot;&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;RIGHTJOIN&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;（右连接）：与&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;LEFTJOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;相反，取得右表（&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table2&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;）完全记录，即是左表（&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;table1&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;）并无匹配对应记录。&lt;/span&gt;\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h3 class=&quot;cjk&quot; style=&quot;font-family:Arial;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;a name=&quot;t2&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;关于&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;MySQLFULL JOIN&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;全连接&lt;/span&gt;&lt;/span&gt;\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;font-family:Arial;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;MySQL&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;没有提供&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'DejaVu Serif\', serif;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;SQL&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:Tahoma, verdana, arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size:9pt;&quot;&gt;标准中的&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:\'Dej