INSERT INTO `by_resume` VALUES ('谢  翔', '1989-09-15', '1', '汉', '大专', '重庆-忠县', '电子商务', '重庆邮电大学', '18502368717', '524414842@qq.com', '2008-09至2011-06   |   重庆邮电大学   |  经济管理学院   |  大专', '应聘职位：PHP程序开发员', 'Dreamweaver、Zend-Studio、Sublime、Notepad、Apache、Nginx、Navicat、wampserver等', '2014.10   |   PHP高级互联网工程师   |   国家工信部（NTC）', '唱歌、弹吉他、书法、烹饪、写作、运动', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n		熟悉使用Apache、Nginx、Wampserver、Navicat、Beyond Compare、Sublime、Zend-Studio等开发工具；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		熟练HTML、CSS、JavaScript等Web前端技术，能够独立开发部分网页，熟练各种JS、Jquery页面特效的灵活运用；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		熟练MySQL数据库对数据的增删改查，数据优化，存储形式；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		熟悉使用PHP面向对象的开发、熟悉PHP框架、理解MVC开发模式；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		熟悉Ajax、Json、正则表达式的运用；\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		熟练Smarty、Yii框架、ThinkPHP框架的运用。\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;', '&lt;table class=&quot;table table-condensed&quot; width=&quot;890&quot; height=&quot;136&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;danger&quot;&gt;\r\n				1、2015-4月至今 &amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp; 重庆博优科技有限公司 &amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp; 职务：PHP程序员\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;工作简述：该公司为外包公司，在该公司主要担任PHP程序开发，从事各种网站、手机APP、微信公众平台、手机网站、积分商城的开发，主要利用纯PHP、ThinkPHP以及内部框架作为开发语言，至今已完成大小项目进30余个。\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;danger&quot;&gt;\r\n				2、2014-7月至2014-11月 &amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp; 重庆触游科技公司 &amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp; 职务：PHP程序员\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;p&gt;\r\n					&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;工作简述：该公司主要从事开发页游、手游和部分网站，我主要从事后台的开发，在手游的开发中主要利用纯PHP代码与前台交互，其余网站部分主要利用ThinkPHP框架开发，在项目经理的带领及规定时间内完成项目开发。\r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;', '     本人诚实守信，工作严谨踏实，认真负责；拥有积极向上的生活态度和广泛的兴趣爱好，具有良好的心理素质和吃苦耐劳精神，有着年轻人特有的朝气和魄力，富有开创事业的头脑和激情，有较好的文字功底；热衷于PHP程序开发，具有良好的学习能力，喜欢专研程序开发；对工作认真负责，逻辑清晰，服从公司安排。');
INSERT INTO `by_talk` VALUES ('1', '5756756', null, null, '7567', '0', '1', '1', '1443087455', '0');
INSERT INTO `by_talk` VALUES ('2', '生命中最值得欣慰的', null, null, '生命中最值得欣慰的，莫过于一觉醒来，你发现太阳还在，你还活着，周围的一切依旧美好。', '0', '1', '0', '1443087545', '0');
INSERT INTO `by_talk` VALUES ('3', '我觉得，因人而异', '/Uploads/talk/pic2015-09-24/5603c5253e0e2.jpg', '5603c5253e0e2.jpg', '不要因为一点瑕疵而放弃一段爱情，毕竟在爱情里，需要的是真情，而不是完美。很多时候，我们视为刻骨铭心的记忆，而别人却早已忘记，与其纠结于心，不如看淡，看轻。', '0', '1', '0', '1443087653', '0');
INSERT INTO `by_talk` VALUES ('4', '坚强心志', null, null, '一个有坚强心志的人，财产可以被人掠夺，勇气却不会被人剥夺的。—— 雨 果', '0', '1', '0', '1443087707', '0');
INSERT INTO `by_talk` VALUES ('5', '时间就是良药', '/Uploads/talk/pic2015-09-24/5603c5b67322a.jpg', '5603c5b67322a.jpg', '时间是最好的良药，随着时间的推移，一切都不再如最初那么刻骨铭心，调整好心态，生命短暂，青春有限，你不会有太多的时间去等待去追忆去痛苦，平常心面对一切，你将会有更多的精力面对未来！', '0', '1', '0', '1443087798', '0');
INSERT INTO `by_talk` VALUES ('6', '扭转乾坤', null, null, 'Sometimes your whole life boils down to one insane move.\r\n人一辈子，有时就靠一次疯狂的举动扭转乾坤。', '0', '1', '0', '1443088039', '0');
INSERT INTO `by_talk` VALUES ('7', '任凭不舍，任凭伤悲', '/Uploads/talk/pic2015-09-24/5603c729623c2.jpg', '5603c729623c2.jpg', '任凭不舍，任凭伤悲，秋风还是如期而至，叶子依旧敌不过季节的更替，敌不过岁月的流失。一夜秋风，满城落叶倾。', '0', '1', '0', '1443088169', '0');
INSERT INTO `by_talk` VALUES ('8', '阡陌红尘', '/Uploads/talk/pic2015-09-25/56050818f1dea.jpg', '56050818f1dea.jpg', '阡陌红尘，终究一场繁花落寞，回忆在岁月中飘落了谁的眼泪，往事在时间中飘落了谁的忧伤。', '0', '1', '0', '1443170328', '0');
INSERT INTO `by_talk` VALUES ('9', '6666666666', null, null, '666666666666666', '0', '1', '1', '1444126870', '0');
INSERT INTO `by_talk` VALUES ('10', '感谢自己', '/Uploads/talk/pic2015-10-06/5613e35e874d2.png', '5613e35e874d2.png', '不管从什么时候开始，重要的是开始以后不要停止；不管在什么时候结束，重要的是结束以后不要后悔。', '0', '1', '0', '1444143966', '0');
INSERT INTO `by_talk` VALUES ('11', '午后的闲暇', '/Uploads/talk/pic2015-10-14/561de093466be.jpg', '561de093466be.jpg', '午后的闲暇总是这么的惬意，搭配暖暖的阳光，许许微风，真想睡一觉！', '0', '0', '0', '1444798611', '0');
INSERT INTO `by_talk` VALUES ('12', '选择', null, null, '人生有很多的选择，时常在想当我站在抉择的十字路口时，我该怎么去选择...换个思维吧，做自己想做的，不怕选择错，既然选择了就应该勇敢的走下去！', '0', '1', '0', '1447512182', '0');
INSERT INTO `by_talk` VALUES ('13', '萧萧冷月风断刀', '/Uploads/talk/pic2015-12-05/5661bdcc0c8c1.png', '5661bdcc0c8c1.png', '风花雪月载流水，醉意且满夜光杯。飘然而去的，只是一个凄美的传奇。怅然间，错过了一段风景，错过了一段情缘。美酒浸泪，人生苦短谁狂笑，萧萧冷月风断刀。', '0', '1', '0', '1449246156', '0');
INSERT INTO `by_talk` VALUES ('14', '善待他人、自己', null, null, '生命太过神秘莫测，人永远不会预测到下一秒会发生什么。这也是生命的唯美之处。确实，我们应该善待、珍惜，不论对自己，亦或对他人。', '0', '1', '0', '1449292002', '0');
INSERT INTO `by_talk` VALUES ('15', '落叶', '/Uploads/talk/pic2015-12-05/566271c84fb18.jpg', '566271c84fb18.jpg', '弄堂的秋天没有枫叶成行的美景，却有着独特的秋色。西风扫过，梧桐树冠抖几下，灿灿的叶儿盘旋着落地，地上顿时有了一地碎金。', '0', '1', '0', '1449292232', '0');
INSERT INTO `by_talk` VALUES ('16', '找回自己', '/Uploads/talk/pic2015-12-09/5667e109efdff.png', '5667e109efdff.png', '一千个人就有一千种生存方式和生活道路，要想改变一些事情，首先得把自己给找回来。', '0', '1', '0', '1449648393', '0');
INSERT INTO `by_talk` VALUES ('17', '人生如絮', '/Uploads/talk/pic2015-12-17/5672c9c30e5c9.png', '5672c9c30e5c9.png', '看过那么多的风景，到头却被风景当成了花絮！', '0', '1', '0', '1450363331', '0');
INSERT INTO `by_talk` VALUES ('18', '戒烟？', null, null, '其实并不是我借不掉烟，我是戒不掉忧愁！', '0', '1', '0', '1450800735', '0');
INSERT INTO `by_talk` VALUES ('19', '悠悠岁月', '/Uploads/talk/pic2016-02-18/56c5398c11ae1.jpg', '56c5398c11ae1.jpg', '人生最美的情感经常出现在我们怀念的时候。悠悠岁月，袅袅情怀，生命的彼岸，总有一处怡人的风景，花开幽香，沁人心怀。伫立在时光的路口，一路走来，山一程水一程，许多人、许多事在岁月的河流中缓缓流过，默默回溯，盘点每一份心情文字，都氤氲着友情和爱意的墨香。心是一缕清风，在弥漫着花香的小径与思念邂逅，脉脉地给心寻找一片惬意和静谧，让灵魂浸润在这婉约的意境里。', '0', '1', '0', '1455765900', '0');
INSERT INTO `by_talk` VALUES ('20', '爱情观', null, null, '如果其他不考虑，谈恋爱是一种信念的执着，是一种互相都有占为己有的冲动，是一种相扶相守共度一生的执念，是一种爱的死心塌地的坚持。否则也只能算是耍流氓', '0', '1', '0', '1451886619', '0');
INSERT INTO `by_talk` VALUES ('21', '我们都生活在一个纷扰的世界里', '/Uploads/talk/pic2016-01-30/56ac1f0bb16f9.jpg', '56ac1f0bb16f9.jpg', '我们都生活在一个纷扰的世界里，每个人的生活都不可能是完美的，世间所有，皆是因缘而生，随缘而去，缘起缘灭，沉浮起落，构成了人生的全部，没有什么是可以永恒的。', '0', '1', '0', '1454120715', '0');
INSERT INTO `by_talk` VALUES ('22', '从心开始，重新开始', '/Uploads/talk/pic2016-01-30/56ac1eddcd2ed.jpg', '56ac1eddcd2ed.jpg', '年底的最后几天班了，心里五味陈杂，又是到了年底自我总结的时候了，收获了什么，失去了什么，这一年算下来其实也真挺伤了，什么都没做好，什么也没留住，过去就过去了吧，从心开始，重新开始，2016·····我来了················', '0', '1', '0', '1454120669', '0');
INSERT INTO `by_talk` VALUES ('23', '新年伊始', '/Uploads/talk/pic2016-02-14/56c038ae42184.jpg', '56c038ae42184.jpg', '2016，新年伊始，万物复苏的季节。再烦，也不忘微笑；再急，也要注意语气；再苦，也别忘坚持；再忙，也不忘珍惜身边的人；再累，也要爱自己。', '0', '1', '0', '1455437998', '0');
INSERT INTO `by_talk` VALUES ('24', '忘记过去', '/Uploads/talk/pic2016-02-18/56c539c27f13e.jpg', '56c539c27f13e.jpg', '一个人总要走陌生的路，看陌生的风景，听陌生的歌。最后你会发现，原本费尽心机想要忘记的事情真的就那么忘记了````` 珍重！', '0', '1', '0', '1455765954', '0');
INSERT INTO `by_talk` VALUES ('25', '离职···', null, null, '这一天，心情还是无比的复杂，待交接工作做完，就要去猪八戒报道了，充满着机遇和挑战，也充满的激情和压力，加油。。。', '0', '1', '0', '1456275438', '0');
